import User from "../models/User.js";
import { configureGemini } from "../config/gemini-config.js";
export const generateChatCompletion = async (req, res, next) => {
    const { message } = req.body;
    try {
        const user = await User.findById(res.locals.jwtData.id);
        if (!user) {
            return res
                .status(401)
                .json({ message: "User not registered OR Token malfunctioned" });
        }
        //select all previous chats
        const chats = user.chats.map(({ role, content }) => ({
            role,
            content,
        }));
        chats.push({ content: message, role: "user" });
        user.chats.push({ content: message, role: "user" });
        // send all chats with new one to Chat completion API
        const config = configureGemini();
        const gemini = config.getGenerativeModel({ model: "gemini-1.5-pro" });
        const chatRequest = {
            history: chats, // Provide the entire chat history
            prompt: message, // The user's new message as a prompt
        };
        const chatResponse = await gemini.generateContent(chatRequest.prompt);
        user.chats.push({ content: chatResponse.response.candidates[0].content.parts[0].text, role: "assistant" });
        await user.save();
        return res.status(200).json({ chats: user.chats });
    }
    catch (error) {
        console.log(error);
        return res.status(201).json({ message: "Something went wrong" });
    }
};
